10596903-7a9b-4ad8-ba58-e0a64507cbdd.template:
{{var "temp" 0}}
{{var "attach" 0}}
{{var "prlEntry" 0}}
{{var "req_type" ""}}

{{#if (or (not type) (eval type "==" ""))}}
  <div style="margin-top: 6px;">◾&nbsp;Missing "type" value</div>
{{/if}}
{{#if (or (not part) (eval part "==" ""))}}
  <div style="margin-top: 6px;">◾&nbsp;Missing "part" value</div>
{{/if}}
{{#eachAttachment}}
  {{set "attach"1}}
{{/eachAttachment}}
{{#if (eval (get "attach") "==" 1)}}
  {{#if (or (not title) (eval title "==" ""))}}
    <div style="margin-top: 6px;">◾&nbsp;Missing "title" for figure</div>
  {{/if}}
{{/if}}
{{#if (and (isSection) (hasChild))}}
  {{set "temp" 0}}
  {{#eachChild}}
    {{#if (isSection)}}
      {{set "temp" 1}}
    {{/if}}
  {{/eachChild}}
  {{#if (and (eval (get "temp") "==" 1) (and text (eval text "!=" "")))}}
    <div style="margin-top: 6px;">◾&nbsp;Hanging text</div>
  {{/if}}
{{else}}
  {{#if (or (not text) (eval text "==" ""))}}
    <div style="margin-top: 6px;">◾&nbsp;Missing text</div>
  {{/if}}
{{/if}}
{{set "prlEntry" 0 }}
{{#if (or (eval type "==" "l") (eval type "==" "l2"))}}
	{{#withParent}}
		{{#if (or (eval type "==" "r") (eval type "==" "m")) }}
			{{set "prlEntry" 1 }}
            {{#if (eval heading "==" "Constraints")}}
              {{set "req_type" "constraint"}}
            {{else}}
              {{set "req_type" ""}}
            {{/if}}
		{{/if}}
	{{/withParent}}
{{/if}}
{{#if (or (eval type "==" "r") (eval type "==" "m"))}}
	{{#if (not (hasChild))}}
		{{set "prlEntry" 1 }}
	{{/if}}
    {{#if (eval heading "==" "Constraints")}}
      {{set "req_type" "constraint"}}
    {{else}}
      {{set "req_type" ""}}
    {{/if}}
{{/if}}
{{#if (eval (get "prlEntry") "==" 1)}}
  {{#if (not conformance)}}
    <div style="margin-top: 6px;">◾&nbsp;Missing conformance</div>
  {{/if}}
  {{#if (eval (get "req_type") "==" "constraint")}}
    {{#if (eval (hasInlinks "constrains") "==" false)}}
      <div style="margin-top: 6px;">◾&nbsp;Missing incoming "constrained" link</div>
    {{/if}}
  {{else}}
    {{#if (and (eval (hasInlinks "fulfils") "==" false) (eval (hasInlinks "fulfil_opt") "==" false))}}
      <div style="margin-top: 6px;">◾&nbsp;Missing incoming "fulfill" link</div>
    {{/if}}
  {{/if}}
{{/if}}
