8e089d69-af5a-4490-ba68-3aa4e3a62e45.template:
{{var "temp" "0"}}
{{var "req_type" ""}}
{{var "sub" 0}}
{{#if (or (not type) (eval type "==" ""))}}
  <div style="margin-top: 6px;">◾&nbsp;Missing "type" value</div>
{{/if}}
{{#if (or (not part) (eval part "==" ""))}}
  <div style="margin-top: 6px;">◾&nbsp;Missing "part" value</div>
{{/if}}
{{#if (and (isSection) (hasChild))}}
  {{set "temp" "0"}}
  {{#eachChild}}
    {{#if (isSection)}}
      {{set "temp" "1"}}
    {{/if}}
  {{/eachChild}}
  {{#if (and (eval (get "temp") "==" "1") (and text (eval text "!=" "")))}}
    <div style="margin-top: 6px;">◾&nbsp;Hanging text</div>
  {{/if}}
{{else}}
  {{#if (or (not text) (eval text "==" ""))}}
    <div style="margin-top: 6px;">◾&nbsp;Missing text</div>
  {{/if}}
{{/if}}
{{#if (or (eval (hasOutlinks "fulfils") "==" true) (eval (hasOutlinks "fulfils_opt") "==" true))}}
  {{#if (or (not conformance) (eval conformance "==" ""))}}
    <div style="margin-top: 6px;">◾&nbsp;Missing a "conformance" value</div>
  {{/if}}
{{/if}}
{{#if (and (eval type "==" "m") (eval heading "==" "Function"))}}
  {{#if (and (eval (hasOutlinks "fulfils") "==" false) (eval (hasOutlinks "supports") "==" false))}}
    <div style="margin-top: 6px;">◾&nbsp;Missing a "fulfill" link</div>
  {{/if}}
{{/if}}
{{#if (or (eval type "==" "l") (eval type "==" "2"))}}
	{{#withParent}}
		{{#if (or (eval type "==" "r") (eval type "==" "m")) }}
            {{#if (searchRegexp (sectionNumber) "^6.4")}}
              {{set "req_type" "f_reqt"}}
            {{/if}}
            {{#if (searchRegexp (sectionNumber) "^6.5")}}
              {{set "req_type" "g_reqt"}}
            {{/if}}
            {{#if (searchRegexp (sectionNumber) "^6.6")}}
              {{set "req_type" "i_reqt"}}
            {{/if}}
		{{/if}}
	{{/withParent}}
{{/if}}
{{#if (or (eval type "==" "r") (eval type "==" "m"))}}
  {{#eachChild}}
    {{#if (or (eval type "==" "r") (or (eval type "==" "m") (eval type "==" "l")))}}
      {{set "sub" 1}}
    {{/if}}
  {{/eachChild}}
  {{#if (eval (get "sub") "!=" 1)}}
            {{#if (searchRegexp (sectionNumber) "^6.4")}}
              {{set "req_type" "f_reqt"}}
            {{/if}}
            {{#if (searchRegexp (sectionNumber) "^6.5")}}
              {{set "req_type" "g_reqt"}}
            {{/if}}
            {{#if (searchRegexp (sectionNumber) "^6.6")}}
              {{set "req_type" "i_reqt"}}
            {{/if}}
  {{/if}}
{{/if}}
{{#if (eval (get "req_type") "!=" "")}}
  {{#if (not conformance)}}
    <div style="margin-top: 6px;">◾&nbsp;Missing conformance</div>
  {{/if}}
  {{#if (and (eval (hasOutlinks "fulfils") "==" false) (eval (hasOutlinks "fulfil_opt") "==" false))}}
      <div style="margin-top: 6px;">◾&nbsp;Missing outgoing "fulfill" link</div>
  {{/if}}
{{/if}}
